import React, { useState } from 'react'

const Button = props => {
	return <button onClick={props.handleClick}>next anecdote</button>
}

const VoteButton = ({ handleClick }) => {
	return <button onClick={handleClick}>vote</button>
}

const Anecdote = ({ anecdote }) => {
	return <p> {anecdote} </p>
}

const MostVoted = ({ winner, votes }) => {
	if (votes === 0) {
		return (
			<div>
				<h2>No quotes voted yet</h2>
			</div>
		)
	}
	return (
		<div>
			<h2>Most voted quote</h2>
			<p>{winner}</p>
		</div>
	)
}

const App = () => {
	const anecdotes = [
		'If it hurts, do it more often',
		'Adding manpower to a late software project makes it later!',
		'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
		'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
		'Premature optimization is the root of all evil.',
		'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.',
		'Programming without an extremely heavy use of console.log is same as if a doctor would refuse to use x-rays or blod tests when dianosing patients'
	]
	const [selected, setSelected] = useState(0)
	const [votes, setVotes] = useState(new Array(7).fill(0))

	const generate = () => {
		let generated = Math.floor(Math.random() * (7 - 1) + 1)
		setSelected(generated)
		return generated
	}

	const voteCount = (votes, selected) => {
		let copyArr = [...votes]
		copyArr[selected] += 1
		setVotes(copyArr)
	}

	const mostVoted = votes.indexOf(Math.max(...votes))
	const minVotes = Math.max(...votes)

	return (
		<div>
			<h1>Anecdote of the day</h1>
			<Anecdote anecdote={anecdotes[selected]} />
			<VoteButton handleClick={() => voteCount(votes, selected)} />
			<Button handleClick={generate} />
			<MostVoted winner={anecdotes[mostVoted]} votes={minVotes} />
		</div>
	)
}

export default App
