import React from 'react'

const Header = props => <h1>{props.header}</h1>

const Part = props => (
	<p>
		{props.part} {props.number}
	</p>
)

const Content = props => {
	return (
		<>
			<Part part={props.parts[0].name} number={props.parts[0].exercises} />
			<Part part={props.parts[1].name} number={props.parts[1].exercises} />
			<Part part={props.parts[2].name} number={props.parts[2].exercises} />
		</>
	)
}

const Total = props => (
	<p>
		Number of exercises
		{/* {props.course.exercises1 +
			props.course.exercises2 +
			props.course.exercises3} */}
	</p>
)

const App = () => {
	const course = 'Half Stack application development'
	const parts = [
		{
			name: 'Fundamentals of React',
			exercises: 10
		},
		{
			name: 'Using props to pass data',
			exercises: 7
		},
		{
			name: 'State of a component',
			exercises: 14
		}
	]

	return (
		<div>
			<Header header={course} />
			<Content parts={parts} />
			<Total parts={parts} />
		</div>
	)
}

export default App
