import React, { useState } from 'react'

const Button = ({ text, handleClick }) => {
	return <button onClick={() => handleClick(text)}>{text}</button>
}

const Results = props => {
	console.log(props)
	return (
		<div>
			<p>Good {props}</p>
			<p>Neutral {props}</p>
			<p>Bad {props}</p>
		</div>
	)
}

const App = () => {
	// save clicks of each button to its own state
	const [good, setGood] = useState(0)
	const [neutral, setNeutral] = useState(0)
	const [bad, setBad] = useState(0)
	const [clicks, setClicks] = useState(0)

	const handleClick = feedback => {
		if (feedback === 'good') {
			console.log(feedback)
			setGood(good + 1)
			setClicks(clicks + 1)
		}
		if (feedback === 'neutral') {
			console.log('neutral')
			setNeutral(neutral + 1)
			setClicks(clicks + 1)
		}
		if (feedback === 'bad') {
			console.log('bad before')
			setBad(bad + 1)
			setClicks(clicks + 1)
		}
	}

	return (
		<div>
			<h1>give feedback</h1>
			<Button text="good" handleClick={handleClick} />
			<Button text="neutral" handleClick={handleClick} />
			<Button text="bad" handleClick={handleClick} />
			<Results good={good} neutral={neutral} bad={bad} />
		</div>
	)
}

export default App
