import React from 'react'

const Header = props => <h1>{props.header}</h1>

const Content = props => {
	return (
		<>
			<p>
				{props.course.part1} {props.course.exercises1}
			</p>
			<p>
				{props.course.part2} {props.course.exercises2}
			</p>
			<p>
				{props.course.part3} {props.course.exercises3}
			</p>
		</>
	)
}

const Total = props => {
	;<p>
		Number of exercises{' '}
		{props.course.exercises1 +
			props.course.exercises2 +
			props.course.exercises3}
	</p>
}
const App = () => {
	const header = 'Half Stack application development'
	const course = {
		part1: 'Fundamentals of React',
		exercises1: 10,
		part2: 'Using props to pass data',
		exercises2: 7,
		part3: 'State of a component',
		exercises3: 14
	}

	return (
		<div>
			<Header header={header} />
			<Content course={course} />
			<Total course={course} />
		</div>
	)
}

export default App
