import React, { useState } from 'react'

const Button = ({ text, handleClick }) => {
	return <button onClick={() => handleClick(text)}>{text}</button>
}

const Results = ({ good, neutral, bad, clicks }) => {
	let average = good + neutral + bad
	return (
		<div>
			<p>Good {good}</p>
			<p>Neutral {neutral}</p>
			<p>Bad {bad}</p>
			<p>Total {clicks}</p>
		</div>
	)
}

const App = () => {
	// save clicks of each button to its own state
	const [good, setGood] = useState(0)
	const [neutral, setNeutral] = useState(0)
	const [bad, setBad] = useState(0)
	const [clicks, setClicks] = useState(0)
	const [scores, setScores] = useState(0)

	const handleClick = feedback => {
		if (feedback === 'good') {
			console.log(feedback)
			setGood(good + 1)
			setClicks(clicks + 1)
		}
		if (feedback === 'neutral') {
			console.log('neutral')
			setNeutral(neutral + 1)
			setClicks(clicks + 1)
		}
		if (feedback === 'bad') {
			console.log('bad before')
			setBad(bad + 1)
			setClicks(clicks + 1)
		}
	}

	return (
		<div>
			<h1>give feedback</h1>
			<Button text="good" handleClick={handleClick} />
			<Button text="neutral" handleClick={handleClick} />
			<Button text="bad" handleClick={handleClick} />
			<Results good={good} neutral={neutral} bad={bad} clicks={clicks} />
		</div>
	)
}

export default App
